import axios from "axios";

import authHeader from './auth-header';

const API_URL = "http://localhost:5000/api/business/uploadCourse";



class Course_Update {


    updating(genres,title,description,selectedFacilitator,startTime,address1,maxMembers,cost) {

        const data =JSON.stringify({
            genres,
            title,
            description,
            selectedFacilitator,
            startTime,
            address1,
            maxMembers,
            cost     

        }) 



        const header={ headers: authHeader()}

        return axios.post(API_URL, 
            data,
            header

        )
    }


}

export default new Course_Update ();
