import React, { Component } from "react";
import { Table, Button, Form ,Accordion,Card} from "react-bootstrap";

import get_course from "../services/get_course";

import CollapsibleCourse from "./CollapsibleCourse";

class CourseApplicationList extends Component {
  constructor() {
    super();
    this.state = {
      data: [],
    
    };


  }

  componentDidMount() {
    get_course.Getting().then((response) => {
      this.setState({
        data: Array.from(response.data),
      });
      console.log(this.state.data);
    });
  }



 

  render() {
    return (
      <div>
        {/* Map each value */}

        {this.state.data.map((singleCourse) => {
          return (

            <Accordion defaultActiveKey="0">
            <Card>
            <Card.Header>
            <Accordion.Toggle as={Button} variant="link" eventKey="0">
                {singleCourse.title}
            </Accordion.Toggle>
            <Button className="float-right" variant="danger" onClick={this.handleClose}>
                Deny
                </Button>
                <Button className="float-right" variant="primary" onClick={this.handleClose}>
                Approve
                </Button>

                </Card.Header>
                <Accordion.Collapse eventKey="0">
                <Card.Body>Hello! I'm the body</Card.Body>
            </Accordion.Collapse>
            </Card>
            </Accordion>
          );
        })}
      </div>
    );
  }
}

export default CourseApplicationList;
