import React, { Component } from "react";
import axios from "axios";
import PaypalButtons from "../Components/PaypalButton"
import "../Styles/CourseDetail.css";
import { Table,Form, Button, Col, Modal } from "react-bootstrap";
import AuthService from "../services/auth.service";
class CourseDetail extends Component {
  constructor(props) {
    super(props);
      this.state = {
          showPaypal : false
      };
  }

  componentDidMount() {
    axios
      .get(`http://localhost:5000/courses/${this.props.match.params.id}`)
      .then((response) => {
        this.setState({
          title: response.data[0].title,
          description: response.data[0].description,
          facilitator: response.data[0].facilitator,
          date: response.data[0].startDate, 
          location: response.data[0].location["address1"],
          maxMembers: response.data[0].maxMembers,
          cost: response.data[0].cost,
            ID: response.data[0]._id
        });
        console.log(this.state.ID);
      })
      .catch((error) => {
        console.log(error);
      });
  }

    showPaypalButtons = () => {
        this.setState({ showPaypal: true });
    };
  render() {
      const { showPaypal } = this.state;
      const currentUser = AuthService.getCurrentUser();
      let button;
      if (!currentUser) {
          button = <Button variant="primary" onClick = {this.showPaypalButtons}  type="button">
              You have to log in first to enroll in a course
              </Button>;
      } else if (currentUser.courseEnrolledIn.includes(this.state.ID)){
          button = <Button variant="primary" onClick = {this.showPaypalButtons}  type="button">
              You have enrolled in this course
              </Button>;
      } else{
          button = <Button variant="primary" onClick = {this.showPaypalButtons}  type="button">
              Buy now for ${this.state.cost}
              </Button>;
      }
      if (showPaypal) {
          return <PaypalButtons
          cost = {this.state.cost}
          title = {this.state.title}
          courseId = {this.state.ID}
          />;
      } else {
    return (
      <div>
        <img src="https://picsum.photos/1000/500" alt="" />
        <p>
          <b>Title: </b>
          {this.state.title}
        </p>
        <p>
          <b>Description: </b>
          {this.state.description}
        </p>
        <p>
          <b>Facilitator: </b>
          {this.state.facilitator}
        </p>
        <p>
          <b>Start Date: </b>
          {this.state.date}
        </p>
        <p>
          <b>Location: </b>
          {this.state.location}
        </p>
        <p>
          <b>Max Members: </b>
          {this.state.maxMembers}
        </p>
        <p>
          <b>Cost: </b>
          {this.state.cost} AUD
        </p>
          {button}

          </div>
    );
  }
      }
}

export default CourseDetail;
