import React, { Component } from "react";
import AuthService from "../services/auth.service";

export default class Profile extends Component {
    constructor(props) {
        super(props);

        this.state = {
            currentUser: AuthService.getCurrentUser()
        };
    }

    componentDidMount() {
        axios
            .post(`http://localhost:5000/api/confirmation/${this.props.match.params.token}`)
            .then((response) => {
                this.setState({
                    message: response.data.cost,
                });
            })
            .catch((error) => {
                console.log(error);
            });
    }
    render() {
        return (
            <div className="col-md-12">
            <div className="card card-container">


            <Form
        onSubmit={this.handleRegister}
        ref={c => {
            this.form = c;
        }}
    >
        {this.state.message && (
        <div className="form-group">
            <div
            className={
                this.state.successful
                    ? "alert alert-success"
                    : "alert alert-danger"
            }
            role="alert"
                >
                {this.state.message}
                </div>
                </div>
        )}

        </Form>
        </div>
        </div>
    );
    }
    
    
}
