import React, { Component } from "react";

import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Table,Form, Button, Col, Modal } from "react-bootstrap";
import { Link } from "react-router-dom";
import course_update from "../services/course_update"
import UserService from "../services/user.service";
import AuthService from "../services/auth.service";



export default class BoardFacilitator extends Component {
    constructor(props) {
        super(props);

        this.state = {
          id: AuthService.getCurrentUser().business._id,
          courses: [],
        };


    }

    componentDidMount() {
        UserService.getBusinessCourses().then(
            response => {
                this.setState({
                    courses: response.data
                });
                console.log(this.state.courses);
                console.log(this.state.courses.length);
            },
        );
    }


    render() {
        return (
            <div>
            <div>
            <h1>{this.state.category}</h1>
            <p>{this.state.description}</p>
            </div>
            <div className="row row-cols-1 row-cols-md-2">
            {this.state.courses.map((course) => {
                    return (
                        <CourseTile
                    category={this.props.match.params.category}
                    title={course.title}
                    description={course.description}
                    key={course._id}
                    id={course._id}
                    />
                );
                })}
            </div>
            </div>
    );
    }
    
}
