import React, { Component } from "react";
import { Table, Button, Form } from "react-bootstrap";
import get_course from "../services/get_course";

import CollapsibleCourse from "./CollapsibleCourse";

class CourseApplicationList extends Component {
  constructor() {
    super();
    this.state = {
      data: [],
    
    };


  }

  componentDidMount() {
    get_course.Getting().then((response) => {
      this.setState({
        data: Array.from(response.data),
      });
      console.log(this.state.data);
    });
  }



 

  render() {
    return (
      <div>
        {/* Map each value */}

        {this.state.data.map((singleCourse) => {
          return (
            <CollapsibleCourse
              courseId={singleCourse._id}
              applicationDate={singleCourse.createdAt}
              courseName={singleCourse.title}
              image="https://picsum.photos/1000/500"
              courseDescription={singleCourse.description}
              facilitator={singleCourse.facilitator}
              date={singleCourse.date}
              address1={singleCourse.location.address1}
              address2={singleCourse.location.address2}
              city={singleCourse.location.city}
              state={singleCourse.location.state}
              zip={singleCourse.location.postCode}
              cost={singleCourse.cost}
              maxMembers={singleCourse.maxMembers}
            />
          );
        })}
      </div>
    );
  }
}

export default CourseApplicationList;
