import React, { Component } from "react";
import { Button } from "react-bootstrap";
import Collapsible from "react-collapsible";
import Approve_course from "../services/approve_course";

function CollapsibleCourse(props) {


  function Approve(id){
    Approve_course.approve(id)
    console.log(id)
  }

  function deny(id){
    Approve_course.approve(id)
    console.log(id)
  }

  return (
    <div style={{ backgroundColor: "white" }}>
      <Collapsible
        //   triggerStyle={{ backgroundColor: "grey" }}  // Does not work because of trigger
        //   trigger=<p>
        //     This is the title <Button>Approve</Button>
        //     <Button>Deny</Button>
        //   </p>
          trigger= {
          <div>
          {props.courseName}
          
          </div>
          }
      >

        <div style={{ backgroundColor: "lightblue" }}>
          <img src={props.image} />
          <p>
            <b>Course ID: </b>
            {props.courseId}
          </p>
          <p>
            <b>Application date: </b>
            {props.applicationDate}
          </p>
          <p>
            <b>Description: </b>
            {props.courseDescription}
          </p>
          <p>
            <b>Facilitator: </b>
            {props.facilitator}
          </p>
          <p>
            <b>Start Date: </b>
            {props.date}
          </p>
          <p>
            <b>Address 1: </b>
            {props.address1}
          </p>
          <p>
            <b>Address 2: </b>
            {props.address2}
          </p>
          <p>
            <b>City: </b>
            {props.city}
          </p>
          <p>
            <b>State: </b>
            {props.state}
          </p>
          <p>
            <b>Zip: </b>
            {props.zip}
          </p>
          <p>
            <b>Cost: </b>
            {props.cost}
          </p>
          <p>
            <b>Max Members: </b>
            {props.maxMembers}
          </p>

        <Button onClick={() => Approve(props.courseId)}  variant="outline-danger" >Approve</Button> {'  '}
          <Button onClick={() => deny(props.courseId)}  variant="outline-danger">Deny</Button>
           </div>
      </Collapsible>
    </div>
  );
}

export default CollapsibleCourse;
