import React, { Component } from "react";

import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Table,Form, Button, Col, Modal } from "react-bootstrap";
import { Link } from "react-router-dom";
import course_update from "../services/course_update"
import UserService from "../services/user.service";
import AuthService from "../services/auth.service";



export default class BoardFacilitator extends Component {
    constructor(props) {
        super(props);

        this.state = {
          id: AuthService.getCurrentUser().business._id,
          courses: [],
        };


    }

    componentDidMount() {
        UserService.getBusinessCourses().then(
            response => {
                this.setState({
                    courses: response.data
                });
                console.log(this.state.courses);
                console.log(this.state.courses.length);
            },
        );
    }


    render() {
        return (
            <div>
            <div>
            <h1><Button variant="primary" onClick={this.handleShow} type="button">
            Upload a course
        </Button></h1>
            </div>
            <div className="row row-cols-1 row-cols-md-2">
            {this.state.courses.map((course) => {
                    return (
                        <div className="col mb-4">
                        <div className="card">
                        <img src="https://picsum.photos/1000/300" alt="" />
                        <div className="card-body">
                        <h5>
                        <Link to={`/courses/${course.category}/${course.id}`}>
                    {course.title}
                </Link>
                    </h5>
                    <p>
                    <b>Facilitator: </b>
                    {course.description}
                </p>
                    <p>
                    <b>Start Date: </b>
                    {course.startDate}
                </p>
                    </div>
                    </div>
                    </div>
                );
                })}
            </div>
            </div>
    );
    }
    
}
