import React, { Component } from "react";
import { Table, Button, Form } from "react-bootstrap";

class CourseApplicationList extends Component {
  constructor() {
    super();
    this.state = {
      courseApplications: [
        ["9/29/2019", "C++ Programming Course"],
        ["9/29/2019", "Self-help Course"],
        ["9/29/2019", "How to hack Monash network"],
      ],
    };
  }

  render() {
    return (
      <div>
        {/* Table */}
        <Table>
          <thead>
            <th>Date (DD-MM-YYYY)</th>
            <th>Course</th>
            <th>Approve</th>
            <th>Deny</th>
          </thead>
          <tbody>
            {/* Map each value */}
            {this.state.courseApplications.map((singleCourse) => {
              return (
                <tr>
                  {/* Need to have the course as a link, to display a full page with the course's details */}
                  <td>{singleCourse[0]}</td>
                  <td>{singleCourse[1]}</td>
                  <td>
                    <Form.Check
                      type="checkbox"
                      // selected={this.state.}
                      // onClick={this.onClick}
                    />
                  </td>
                  <td>
                    <Form.Check type="checkbox" />
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
        {/* Button to submit choices */}
        <Button type="submit">Submit</Button>
      </div>
    );
  }
}

export default CourseApplicationList;
