import React, { Component } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

// This is a sample component. It demonstrates how adding information
// from the user into the database. Could be used for things such as
// signing up, or a questionnaire.

class AddInfoToDatabase extends Component {
  constructor(props) {
    super(props);

    this.state = {
      property1: "",
      property2: "",
      property3: 0,
      property4: new Date(),
      property5: [],
    };

    this.onChangeProperty1 = this.onChangeProperty1.bind(this);
    this.onChangeProperty2 = this.onChangeProperty2.bind(this);
    this.onChangeProperty3 = this.onChangeProperty3.bind(this);
    this.onChangeProperty4 = this.onChangeProperty4.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  // Automatically called before anything is displayed on the page. Before anything loads, this code is run.
  componentDidMount() {
    // Set the state of a property automatically
    this.setState({
      property5: ["A", "B", "C", "D", "E"],
    });
  }

  //   Methods to change state

  onChangeProperty1(e) {
    this.setState({
      // e.target.value is the input on the webpage from the user. The state changes to reflect what has been entered
      property1: e.target.value,
    });
  }

  onChangeProperty2(e) {
    this.setState({
      property2: e.target.value,
    });
  }

  onChangeProperty3(e) {
    this.setState({
      property3: e.target.value,
    });
  }

  onChangeProperty4(date) {
    this.setState({
      property4: date,
    });
  }

  // Method to handle form submission
  onSubmit(e) {
    e.preventDefault(); // Prevent the default html submit behaviour

    const exampleVar = {
      property1: this.state.property1,
      property2: this.state.property2,
      property3: this.state.property3,
      property4: this.state.property4,
    };

    console.log(exampleVar);
    // This is the part where we submit the data to the database

    // After the data has been submitted...
    window.location = "/"; // Takes the user back to the homepage
  }

  render() {
    return (
      <div>
        <h3>Sample Entry Forum</h3>
        <form onSubmit={this.onSubmit}>
          {/* Dropdown select for property 1. The values come from property 5, from the database. */}
          <div className="form-group">
            <label>Property1: </label>
            <select
              ref="userInput"
              required
              className="form-control"
              value={this.state.property1}
              onChange={this.onChangeProperty1}
            >
              {this.state.property5.map(function (sampleValue) {
                return (
                  <option key={sampleValue} value={sampleValue}>
                    {sampleValue}
                  </option>
                );
              })}
            </select>
          </div>

          {/* Text input box for property 2 (Text). */}
          <div className="form-group">
            <label>Property2: </label>
            <input
              type="text"
              required
              className="form-control"
              value={this.state.property2}
              onChange={this.onChangeProperty2}
            />
          </div>

          {/* Text input box for property 3 (Integer). */}
          <div className="form-group">
            <label>Property3: </label>
            <input
              type="text"
              required
              className="form-control"
              value={this.state.property3}
              onChange={this.onChangeProperty3}
            />
          </div>

          {/* Text input box for property 4 (Date). */}
          <div className="form-group">
            <label>Property4: </label>
            <div>
              {/* DatePicker component pops up a calendar to pick a date. Install a package with npm */}
              <DatePicker
                selected={this.state.property4} // Initial selection
                onChange={this.onChangeProperty4}
              />
            </div>
          </div>

          {/* Button */}
          <div className="form-group">
            <input type="submit" value="Submit" className="btn btn-primary" />
          </div>
        </form>
      </div>
    );
  }
}

export default AddInfoToDatabase;
