import React, { Component } from "react";
import { Table, Button, Form } from "react-bootstrap";
import get_Business from "../services/get_Business";
import approve_business from "../services/approve_business";

class BusinessApplicationList extends Component {
  constructor() {
    super();
    this.state = {
      data: [],
      businessSelected: [],
      denyBusiness:[],
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.submit = this.submit.bind(this);
    this.denySubmit=this.denySubmit.bind(this);
  }

  componentDidMount() {
    get_Business.Getting().then(
        response => {
            this.setState({
              data: Array.from(response.data)
            });
            console.log(this.state.data)
        },

    );
}


handleSubmit(x){

  var value = this.state.businessSelected.includes(x)
  if(value === false){
    this.setState({businessSelected: this.state.businessSelected.concat(x) },()=>{
      //console.log(this.state.businessSelected)
    }

    );

  } 
}

denySubmit(x){

  var value = this.state.denyBusiness.includes(x)
  if(value === false){
    this.setState({denyBusiness: this.state.denyBusiness.concat(x) },()=>{

    }
    );
  } 
}


submit(){
  this.state.businessSelected.map((item) => {
    approve_business.approve(item)
  })

  this.state.denyBusiness.map((item) => {
    approve_business.deny(item)
  })
}


  render() {
    return (
      <div>
        {/* Table */}
        <Table>
          <thead>
            <th>Business Name</th>
            <th>Business Decription</th>
            <th>Username</th>
            <th>Approve</th>
            <th>Deny</th>
          </thead>
          <tbody>
            {/* Map each value */}
            {this.state.data.map((business) => {
              return (
                <tr>
                  {/* Need to have the course as a link, to display a full page with the course's details */}
                  <td>{business[1].businessName}</td>
                  <td>{business[1].decriptionOfBusiness}</td>
                  <td>{business[0].username}</td>
                  <td>
                    <Form.Check
                      type="checkbox"

                      onClick={() => this.handleSubmit(business[0]._id)}
                    />
                  </td>
                  <td>
                    <Form.Check
                      type="checkbox"
                      // selected={this.state.}
                      onClick={() => this.denySubmit(business[0]._id)}
                    />
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
        {/* Button to submit choices */}
        <Button type="submit"

        onClick={this.submit}
        >Submit</Button>
      </div>
    );
  }
}

export default BusinessApplicationList;
