import React, { Component } from "react";
import { Table, Button, Form } from "react-bootstrap";
import get_course from "../services/get_course"

class CourseApplicationList extends Component {
  constructor() {
    super();
    this.state = {


      data: [],
    };
  }

  componentDidMount() {
    get_course.Getting().then(
        response => {
            this.setState({

                data: Array.from(response.data)
            });

        },

    );
}


  render() {
    return (
      <div>
        {/* Table */}
        <Table>
          <thead>
            <th>Date (DD-MM-YYYY)</th>
            <th>Course</th>
            <th>Approve</th>
            <th>Deny</th>
          </thead>
          <tbody>
            {/* Map each value */}

            {this.state.data.map((singleCourse) => {
              return (
                <tr>
                  {/* Need to have the course as a link, to display a full page with the course's details */}
                  <td>{singleCourse.date}</td>
                  <td>{singleCourse.title}</td>
                  <td>
                    <Form.Check
                      type="checkbox"
                      // selected={this.state.}
                      // onClick={this.onClick}
                    />
                  </td>
                  <td>
                    <Form.Check type="checkbox" />
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
        {/* Button to submit choices */}
        <Button type="submit">Submit</Button>
      </div>
    );
  }
}

export default CourseApplicationList;
