import React, { Component } from "react";
import axios from "axios";
import "../Styles/CourseDetail.css";

class BusinessCourseDetail extends Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    componentDidMount() {
        axios
            .get(`http://localhost:5000/courses/${this.props.match.params.id}`)
            .then((response) => {
                this.setState({
                    title: response.data[0].title,
                    description: response.data[0].description,
                    facilitator: response.data[0].facilitator,
                    date: response.data[0].startDate,
                    location: response.data[0].location["address1"],
                    maxMembers: response.data[0].maxMembers,
                    cost: response.data[0].cost,
                });
                console.log(response.data[0])
            })
            .catch((error) => {
                console.log(error);
            });
    }
    componentDidMount() {
        UserService.getBusinessOneCourse(this.props.match.params.id).then(
            response => {
                this.setState({
                    course: response.data
                });
                console.log(this.state.course);
            },
        );
    }

    render() {
        return (
            <div>
            <img src="https://picsum.photos/1000/500" alt="" />
            <p>
            <b>Title: </b>
        {this.state.course.title}
    </p>
        <p>
        <b>Description: </b>
        {this.state.course.description}
    </p>
        <p>
        <b>Facilitator: </b>
        {this.state.course.facilitator}
    </p>
        <p>
        <b>Date: </b>
        {new Date(this.state.course.startDate).toISOString().replace('-', '/').split('T')[0].replace('-', '/').concat(
            " - ",
            new Date(this.state.course.endDate).toISOString().replace('-', '/').split('T')[0].replace('-', '/')
        )}
    </p>

        </div>
    );
    }
}

export default BusinessCourseDetail;
