import axios from "axios";

import authHeader from './auth-header';

const API_URL = "http://localhost:5000/api/business/uploadCourse";



class Course_Update {


    updating() {

        let data ={
            "category":"programming",
            "title":"OO Design",
            "description":"Hello World",
            "facilitator":"Bill Quan",
            "date":"9/14/2020",
            "location":"Clayton",
            "maxMembers":"2000000",
            "cost":"40"
          }



        let header={ headers: authHeader()}

        return axios.post(API_URL, {
            header,
            data
        });
    }


}

export default new Course_Update ();
