import axios from 'axios';
import authHeader from './auth-header';

const API_URL = 'http://localhost:5000/api/';

class UserService {
    getPublicContent() {
        return axios.get(API_URL + 'test/all')
    }

    getUserBoard() {
        return axios.get(API_URL + 'test/user', { headers: authHeader() });
    }
    
    getBusinessCourses(){
        return axios.get(API_URL + 'business/viewCourses', { headers: authHeader() });
    }
    getBusinessOneCourse(courseId){
        const data=JSON.stringify({
            "courseId":courseId
        });
        return axios.post(API_URL + 'business/viewOneCourse', data, { headers: authHeader() });
    }
    getBusinessAccounts(){
        return axios.get(API_URL + 'admin/getBusinessAccounts', { headers: authHeader() });
    }
    getBusinessCoursesInAdminPage(userId){
        const data=JSON.stringify({
            "userId":userId
        });
        return axios.post(API_URL + 'admin/viewCoursesInAdminPage', data, { headers: authHeader() });
    }
    getBusinessOneCourseInAdminPage(userId,courseId){
        const data=JSON.stringify({
            "userId":userId,
            "courseId":courseId
        });
        return axios.post(API_URL + 'admin/viewOneCourseInAdminPage', data, { headers: authHeader() });
    }
    enrollCourse(userId,courseId,cost,payerId,orderId){
        console.log(userId);
        const data=JSON.stringify({
            "userId":userId,
            "courseId":courseId,
            "cost":cost,
            "payerId":payerId,
            "orderId":orderId,
        });
        console.log(data);
        return axios.post(API_URL + 'enroll', data, { headers: authHeader() });
    }
}

export default new UserService();
