  constructor() {
    super();
    this.state = {
      data: [],
      courseselected: [],
    };  

    this.handleSubmit = this.handleSubmit.bind(this);

  }


  componentDidMount() {
    get_course.Getting().then(
        response => {
            this.setState({
                data: Array.from(response.data)
            });
            console.log(this.state.data)
        },

    );
}




