import React, { Component } from "react";
import { Table, Button, Form } from "react-bootstrap";
import get_course from "../services/get_course";
import Approve_course from "../services/approve_course";

class CourseApplicationList extends Component {
  constructor() {
    super();
    this.state = {
      data: [],
      courseselected: [],
      denyCourse: [],
    };  

    this.handleSubmit = this.handleSubmit.bind(this);
    this.submit = this.submit.bind(this);
    this.denySubmit = this.denySubmit.bind(this);
  }



  componentDidMount() {
    get_course.Getting().then(
        response => {
            this.setState({
                data: Array.from(response.data)
            });
            console.log(this.state.data)
        },

    );
}




handleSubmit =(x) => {

  var value = this.state.courseselected.includes(x) 
  if(value === false){
  this.setState({courseselected: this.state.courseselected.concat(x) },()=>{
    //console.log(this.state.courseselected)
  }

  );  
}  
}




denySubmit =(x) => {

  var value = this.state.denyCourse.includes(x) 
  if(value === false){
  this.setState({denyCourse: this.state.denyCourse.concat(x) },()=>{
  }

  );  
}  
}

submit(){
  this.state.courseselected.map((item) => {
    Approve_course.approve(item)
  })

  this.state.denyCourse.map((item) => {
    Approve_course.deny(item)
  })
}




  render() {
    return (
      <div>
        {/* Table */}
        <Table>
          <thead>
            <th>Course </th>
            <th>Facilitator</th>
            <th>Approve</th>
            <th>Deny</th>
          </thead>
          <tbody>
            {/* Map each value */}

            {this.state.data.map((singleCourse) => {
              return (
                <tr>
                  {/* Need to have the course as a link, to display a full page with the course's details */}
                  <td>{singleCourse.title}</td>
                  <td>{singleCourse.facilitator}</td>
                  <td>
                    <Form.Check
                      type="checkbox"                    
                      onClick={() => this.handleSubmit(singleCourse._id)}
                      //onClick={this.handleSubmit}

                    />
                  </td>
                  <td>
                    <Form.Check type="checkbox" 
                    onClick={() => this.denySubmit(singleCourse._id)}

                    />
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
        {/* Button to submit choices */}
        <Button type="submit"

        onClick={this.submit}
        >Submit</Button>




      </div>
    );
  }
}

export default CourseApplicationList;
