import React, { Component } from "react";
import axios from "axios";
import "../Styles/CourseDetail.css";

class CourseDetail extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  componentDidMount() {
    axios
      .get(`http://localhost:5000/courses/${this.props.match.params.id}`)
      .then((response) => {
        this.setState({
          title: response.data[0].title,
          description: response.data[0].description,
          facilitator: response.data[0].facilitator,
          date: response.data[0].startDate, 
          location: response.data[0].location[address1],
          maxMembers: response.data.maxMembers,
          cost: response.data[0].cost,
        });
        console.log(response.data[0])
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    return (
      <div>
        <img src="https://picsum.photos/1000/500" alt="" />
        <p>
          <b>Title: </b>
          {this.state.title}
        </p>
        <p>
          <b>Description: </b>
          {this.state.description}
        </p>
        <p>
          <b>Facilitator: </b>
          {this.state.facilitator}
        </p>
        <p>
          <b>Start Date: </b>
          {this.state.date}
        </p>
        <p>
          <b>Location: </b>
          {this.state.location}
        </p>
        <p>
          <b>Max Members: </b>
          {this.state.maxMembers}
        </p>
        <p>
          <b>Cost: </b>
          {this.state.cost}
        </p>
      </div>
    );
  }
}

export default CourseDetail;
