import axios from "axios";

import authHeader from './auth-header';

const API_URL = "http://localhost:5000/api/admin/approveBusiness ";



class Approve_business {


    approve(id) {

        const header={ headers: authHeader()}

        const data =JSON.stringify({
             "userId":id,
             "approve":true


        }) 

        return axios.post(API_URL, 
            data,
            header  
        ).then(
            console.log("successfully")
        )


    }

    deny(id) {

        const header={ headers: authHeader()}

        const data =JSON.stringify({
             "userId":id,
             "approve":false


        }) 

        return axios.post(API_URL, 
            data,
            header  
        ).then(
            console.log("successfully")
        )


    }

}

export default new Approve_business();
