import React, { Component } from "react";

import UserService from "../services/user.service";
import { Table,Form, Button, Col, Modal } from "react-bootstrap";
import { Link } from "react-router-dom";


export default class BoardAdmin extends Component {
    constructor() {
        super();
        this.state = {
          courseApplications: [],
          businesses:[]
        };
      }

    componentDidMount() {
        UserService.getBusinessAccounts().then(
            response => {
                this.setState({
                    businesses: response.data
                });
                console.log(this.state.businesses);
                console.log(this.state.businesses.length);
            },
        );
    }
    

      
      render() {
        return (
          <div>
            {/* Title */}
            <h1>Admin Space</h1>
    
            {/* Buttons */}
            <div>
              <Button href="adminApproval/courses">Course Applications</Button>
              <Button href="adminApproval/businesses">Business Applications</Button>
            </div>
            <div>
              <h3>Business Account List</h3>
              </div>
              {this.state.businesses.map((business) => {
                      return (
                          <div>
                          <h5>
                          <Link to={`/admin/${business[0]._id}`}>
                      {business[1].businessName}
                  </Link>
                      </h5>
                      <p>
                      <b>Business Structure: </b>
                      {business[1].businessStructure}
                  </p>
                  <p>
                  <b>Phone: </b>
                  {business[1].phone}
              </p>
                  </div>
                  );
                  })}

          </div>
        );
      }
    
}
