import axios from "axios";

import authHeader from './auth-header';

const API_URL = "http://localhost:5000/api/admin/approveCourse ";



class Approve_course {


    approve(id) {

        const header={ headers: authHeader()}

        const data =JSON.stringify({
             "courseId":id,
             "approve":true


        }) 

        return axios.post(API_URL, 
            data,
            header  
        )


    }

    deny(id) {

        const header={ headers: authHeader()}

        const data =JSON.stringify({
             "courseId":id,
             "approve":false


        }) 

        return axios.post(API_URL, 
            data,
            header  
        )


    }


}

export default new Approve_course ();
